mapper.select:
select r.id,r.type,r.name,b.create_date,r.browsed_times from browse_history b,resource r where b.rid=r.id and b.creator=#{creator}
and r.keyword like '%${keyword}%'
and r.type in
#{type}
order by b.create_date desc

select count(*) from (select distinct r.id from browse_history b,resource r where b.rid=r.id and b.creator=#{creator} and r.type = #{type}
and r.keyword like '%${keyword}%'
) a

select r.id,r.type,r.name,c.create_date,r.browsed_times from collection c,resource r where c.rid=r.id and c.del=0 and c.creator=#{creator}
and r.keyword like '%${keyword}%'
and r.type in
#{type}
order by c.create_date desc
